import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../Dashboard.css"; // Reuse Dashboard styles

const AdminDashboard = () => {
  const [stats, setStats] = useState({
    users: 0,
    jobs: 0,
    applications: 0,
  });
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    fetchStats();
  }, []);

  const fetchStats = async () => {
    try {
      const token = localStorage.getItem("token");
      const [usersRes, jobsRes, appsRes] = await Promise.all([
        axios.get("/api/users", {
            headers: { Authorization: `Bearer ${token}` }
        }),
        axios.get("/api/jobs", {
            headers: { Authorization: `Bearer ${token}` }
        }),
        axios.get("/api/applications/all", {
            headers: { Authorization: `Bearer ${token}` }
        })
      ]);

      setStats({
        users: usersRes.data.length,
        jobs: jobsRes.data.length,
        applications: appsRes.data.count || 0,
      });
    } catch (error) {
      console.error("Error fetching admin stats:", error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div className="p-8 text-slate-500">Loading system overview...</div>;

  return (
    <div className="dashboard-container fade-in">
      <div className="dashboard-header">
        <h1 className="welcome-title">Admin Console</h1>
        <p className="welcome-subtitle">System performance and user management.</p>
      </div>

      <div className="stats-row">
        <div className="stat-card" onClick={() => navigate("/admin/users")}>
          <div className="stat-header">
             <div className="stat-title">Total Users</div>
             <div className="stat-icon-wrapper bg-blue-light">ðŸ‘¥</div>
          </div>
          <div className="stat-value">{stats.users}</div>
          <div className="stat-trend trend-up">â†— 12% <span className="text-slate-400 ml-1">growth</span></div>
        </div>

        <div className="stat-card" onClick={() => navigate("/admin/jobs")}>
          <div className="stat-header">
             <div className="stat-title">Active Jobs</div>
             <div className="stat-icon-wrapper bg-purple-light">ðŸ’¼</div>
          </div>
          <div className="stat-value">{stats.jobs}</div>
          <div className="stat-trend trend-neutral">Stable</div>
        </div>

        <div className="stat-card">
          <div className="stat-header">
             <div className="stat-title">Applications</div>
             <div className="stat-icon-wrapper bg-amber-light">ðŸ“„</div>
          </div>
          <div className="stat-value">{stats.applications}</div>
          <div className="stat-trend trend-up">â†— All time</div>
        </div>

        <div className="stat-card">
          <div className="stat-header">
             <div className="stat-title">System Health</div>
             <div className="stat-icon-wrapper bg-green-light">âš¡</div>
          </div>
          <div className="stat-value">99.9%</div>
          <div className="stat-trend trend-up">Operational</div>
        </div>
      </div>

      <div className="dashboard-content-grid">
         {/* Main Column: Quick Actions & Management */}
         <div className="main-column">
            <section className="card p-6 border border-slate-200">
               <div className="section-header">
                  <div className="section-title">Platform Management</div>
               </div>
               <div className="grid grid-cols-2 gap-4">
                  <button className="btn btn-primary" onClick={() => navigate('/admin/users')}>
                     Manage Users
                  </button>
                  <button className="btn btn-outline" onClick={() => navigate('/admin/jobs')}>
                     Audit Jobs
                  </button>
               </div>
            </section>
         </div>

         {/* Right Sidebar: Logs */}
         <aside className="right-sidebar">
            <div className="sidebar-card">
               <div className="section-header">
                  <div className="section-title">System Logs</div>
               </div>
               <div className="notification-list">
                  <div className="notif-item info">
                     <div className="notif-title">System Backup</div>
                     <span className="notif-desc">Backup completed successfully.</span>
                     <span className="notif-date">1 hour ago</span>
                  </div>
                  <div className="notif-item">
                     <div className="notif-title">New User Registration</div>
                     <span className="notif-desc">+5 new users today.</span>
                     <span className="notif-date">4 hours ago</span>
                  </div>
               </div>
            </div>
         </aside>
      </div>
    </div>
  );
};

export default AdminDashboard;
