import React, { useState } from "react";
import Sidebar from "./Sidebar";
import Header from "./Header";
import ChatSystem from "./ChatSystem";
import "./Layout.css";

function Layout({ children }) {
  const [showChat, setShowChat] = useState(false);
  const user = JSON.parse(localStorage.getItem("user"));

  return (
    <div className="app-layout">
      <Sidebar /> 
      
      <div className="layout-main-content">
        <div className="layout-header-wrapper">
          <Header />
        </div>
        <main className="layout-page-content">
          {children}
        </main>
        
        {/* Chat Floating Action Button */}
        {user && (
            <>
                <button 
                    className="fixed bottom-6 right-6 w-14 h-14 bg-primary text-white rounded-full shadow-lg flex items-center justify-center text-2xl z-50 hover:bg-blue-600 transition-colors"
                    onClick={() => setShowChat(!showChat)}
                >
                    ðŸ’¬
                </button>
                {showChat && (
                    <div className="fixed bottom-24 right-6 z-50">
                        <ChatSystem 
                            currentUser={user} 
                            chatPartnerId={user.role === 'student' ? 2 : 1} // Mock partner for demo
                            chatPartnerName={user.role === 'student' ? "Recruiter" : "Student"} 
                            roomId="demo-room" 
                            onClose={() => setShowChat(false)}
                        />
                    </div>
                )}
            </>
        )}
      </div>
    </div>
  );
}

export default Layout;
