import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './JobList.css';

const JobList = () => {
  const [jobs, setJobs] = useState([]);
  const [filteredJobs, setFilteredJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchTerm, setSearchTerm] = useState("");
  const [filters, setFilters] = useState({
    location: "",
    job_type: [],
    experience_level: []
  });
  
  const navigate = useNavigate();

  useEffect(() => {
    fetchJobs();
  }, []);

  useEffect(() => {
    applyFilters();
  }, [jobs, searchTerm, filters]);

  const fetchJobs = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('/api/jobs', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setJobs(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching jobs:', error);
      setLoading(false);
    }
  };

  const applyFilters = () => {
    let result = jobs;

    if (searchTerm) {
      result = result.filter(job => 
        job.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        job.company.toLowerCase().includes(searchTerm.toLowerCase()) ||
        job.skills_required.some(skill => skill.toLowerCase().includes(searchTerm.toLowerCase()))
      );
    }

    if (filters.location) {
      result = result.filter(job => 
        job.location.toLowerCase().includes(filters.location.toLowerCase())
      );
    }

    if (filters.job_type.length > 0) {
      result = result.filter(job => filters.job_type.includes(job.job_type));
    }

    if (filters.experience_level.length > 0) {
      result = result.filter(job => filters.experience_level.includes(job.experience_level));
    }

    setFilteredJobs(result);
  };

  const handleCheckboxChange = (category, value) => {
    setFilters(prev => {
      const current = prev[category];
      const updated = current.includes(value)
        ? current.filter(item => item !== value)
        : [...current, value];
      return { ...prev, [category]: updated };
    });
  };

  const handleApply = async (jobId) => {
    try {
      const token = localStorage.getItem('token');
      await axios.post('/api/applications', { job_id: jobId }, {
        headers: { Authorization: `Bearer ${token}` }
      });
      alert('Application successful!');
    } catch (error) {
      alert(error.response?.data?.error || 'Failed to apply');
    }
  };

  if (loading) return <div className="p-8 text-center text-slate-500">Loading opportunities...</div>;

  return (
    <div className="job-explorer fade-in">
      <header className="job-explorer-header">
        <h2 className="font-[Outfit]">Internships</h2>
        <p className="text-slate-500">Apply to {filteredJobs.length} {filters.job_type.includes('Internship') ? 'internships' : 'opportunities'} matching your preferences</p>
      </header>

      <div className="job-explorer-content">
        {/* Filters Sidebar */}
        <aside className="filters-sidebar">
           <div className="filters-card">
              <div className="flex-between mb-6">
                <h4 className="m-0 text-slate-700 font-[Outfit]">Filters</h4>
                <button 
                  className="text-xs text-primary font-bold bg-transparent border-0 cursor-pointer hover:underline"
                  onClick={() => setFilters({ job_type: [], experience_level: [], location: "" })}
                >
                  Clear all
                </button>
              </div>

              <div className="filter-group">
                <span className="filter-label">Location</span>
                <input 
                  type="text" 
                  placeholder="e.g. Delhi, Mumbai" 
                  className="input-field text-sm mb-2"
                  value={filters.location}
                  onChange={(e) => setFilters({...filters, location: e.target.value})}
                />
              </div>

              <div className="filter-group">
                <span className="filter-label">Profile</span>
                <input 
                  type="text" 
                  placeholder="e.g. Marketing" 
                  className="input-field text-sm mb-2"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>

              <div className="filter-group">
                <span className="filter-label">Type</span>
                {['Full-time', 'Internship', 'Contract'].map(type => (
                  <div key={type} className="checkbox-field">
                    <input 
                      type="checkbox" 
                      id={type} 
                      checked={filters.job_type.includes(type)}
                      onChange={() => handleCheckboxChange('job_type', type)}
                    />
                    <label htmlFor={type} className="checkbox-label">{type}</label>
                  </div>
                ))}
              </div>

              <div className="filter-group">
                <span className="filter-label">Experience</span>
                {['Entry', 'Intermediate', 'Senior'].map(exp => (
                  <div key={exp} className="checkbox-field">
                    <input 
                      type="checkbox" 
                      id={exp}
                      checked={filters.experience_level.includes(exp)}
                      onChange={() => handleCheckboxChange('experience_level', exp)}
                    />
                    <label htmlFor={exp} className="checkbox-label">{exp}</label>
                  </div>
                ))}
              </div>
           </div>
        </aside>

        {/* Job Results Container */}
        <main className="job-results-container">
           {filteredJobs.length > 0 ? filteredJobs.map(job => (
             <div key={job.id} className="job-card">
                <div className="job-card-header">
                   <div>
                      <h3 className="job-card-title">{job.title}</h3>
                      <p className="job-card-company">{job.company}</p>
                   </div>
                   <div className="job-status-badge active">
                      <span className="w-2 h-2 rounded-full bg-success"></span>
                      Actively Hiring
                   </div>
                </div>

                <div className="job-details-grid">
                   <div className="detail-item">
                      <span className="detail-label">üìç Location</span>
                      <span className="detail-value">{job.location}</span>
                   </div>
                   <div className="detail-item">
                      <span className="detail-label">üí∞ Stipend</span>
                      <span className="detail-value">{job.salary_range}</span>
                   </div>
                   <div className="detail-item">
                      <span className="detail-label">‚è≥ Duration</span>
                      <span className="detail-value">6 Months</span>
                   </div>
                   <div className="detail-item">
                      <span className="detail-label">üéì Experience</span>
                      <span className="detail-value">{job.experience_level}</span>
                   </div>
                </div>

                <div className="job-card-footer">
                   <div className="job-tags">
                      <span className="job-tag bg-slate-100 text-slate-600">{job.job_type}</span>
                      {job.skills_required?.slice(0, 2).map(skill => (
                        <span key={skill} className="job-tag bg-primary-soft text-primary">{skill}</span>
                      ))}
                   </div>
                   <div className="job-actions">
                      <button className="btn btn-outline" onClick={() => navigate(`/jobs/${job.id}`)}>View Details</button>
                      <button className="btn btn-primary" onClick={() => handleApply(job.id)}>Apply Now</button>
                   </div>
                </div>
             </div>
           )) : (
             <div className="no-jobs-state">
                <div className="no-jobs-icon">üîç</div>
                <h3>No opportunities found</h3>
                <p className="text-slate-500">Try adjusting your filters or search terms.</p>
             </div>
           )}
        </main>
      </div>
    </div>
  );
};

export default JobList;
