import React, { useState, useEffect } from "react";
import axios from "axios";
import "./SkillsManager.css";

function SkillsManager() {
  const [allSkills, setAllSkills] = useState([]);
  const [mySkills, setMySkills] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedProficiency, setSelectedProficiency] = useState("Beginner");
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  
  // New Profile State
  const token = localStorage.getItem("token");
  const user = JSON.parse(localStorage.getItem("user"));
  
  const [portfolioUrl, setPortfolioUrl] = useState(user?.portfolio_url || "");
  const [githubUrl, setGithubUrl] = useState(user?.github_url || "");
  const [resumeUrl, setResumeUrl] = useState(user?.resume_url || "");
  const [uploading, setUploading] = useState(false);

  // Fetch skills on load
  useEffect(() => {
    fetchSkills();
  }, []);

  const fetchSkills = async () => {
    try {
      setLoading(true);
      setError("");

      const allRes = await axios.get("/api/skills/all");
      setAllSkills(allRes.data);

      const myRes = await axios.get(
        "/api/skills/my-skills",
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setMySkills(myRes.data);
      setLoading(false);
    } catch (error) {
      console.error("‚ùå Error fetching skills:", error);
      setError("Failed to load skills. Please try again.");
      setLoading(false);
    }
  };

  const handleFileUpload = async (e) => {
      const file = e.target.files[0];
      if (!file) return;

      const formData = new FormData();
      formData.append("file", file);

      try {
          setUploading(true);
          const res = await axios.post("/api/upload", formData, {
              headers: { "Content-Type": "multipart/form-data" }
          });
          setResumeUrl(res.data.url);
          alert("Resume uploaded successfully!");
      } catch (err) {
          alert("Upload failed. Keep file size under 5MB.");
      } finally {
          setUploading(false);
      }
  };

  const handleSaveProfile = async () => {
      try {
          await axios.put("/api/users/profile", {
              portfolio_url: portfolioUrl,
              github_url: githubUrl,
              resume_url: resumeUrl
          }, {
              headers: { Authorization: `Bearer ${token}` }
          });
          
          // Update local user 
          const updatedUser = { ...user, portfolio_url: portfolioUrl, github_url: githubUrl, resume_url: resumeUrl };
          localStorage.setItem("user", JSON.stringify(updatedUser));
          alert("Profile updated successfully!");
      } catch (err) {
          alert("Failed to update profile.");
      }
  };

  const addSkill = async (skillId, skillName) => {
    try {
      const response = await axios.post(
        "/api/skills",
        { skillId, proficiency: selectedProficiency },
        { headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` } }
      );

      if (response.data.success) {
        alert(`‚úÖ ${skillName} added successfully!`);
        fetchSkills();
      } else {
        alert(`‚ùå ${response.data.error || "Failed to add skill"}`);
      }
    } catch (error) {
      console.error("Error adding skill:", error);
      alert("Failed to add skill");
    }
  };

  const removeSkill = async (userSkillId, skillName) => {
    if (!window.confirm(`Are you sure you want to remove "${skillName}"?`)) return;

    try {
      const response = await axios.delete(
        `/api/skills/${userSkillId}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      if (response.data.success) {
        alert(`‚úÖ ${skillName} removed successfully!`);
        fetchSkills();
      }
    } catch (error) {
      console.error("Error removing skill:", error);
      alert("‚ùå Failed to remove skill");
    }
  };

  const updateProficiency = async (userSkillId, proficiency, skillName) => {
    try {
      await axios.put(
        `/api/skills/${userSkillId}`,
        { proficiency },
        { headers: { "Content-Type": "application/json", Authorization: `Bearer ${token}` } }
      );

      setMySkills(
        mySkills.map((skill) =>
          skill.id === userSkillId ? { ...skill, proficiency } : skill
        )
      );
    } catch (error) {
      console.error("Error updating proficiency:", error);
      alert(`‚ùå Failed to update ${skillName}`);
    }
  };

  // IDs of skills user already has
  const mySkillIds = mySkills.map((s) => s.skill_id);

  // Filter available skills
  const availableSkills = allSkills.filter(
    (skill) =>
      !mySkillIds.includes(skill.id) &&
      skill.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  if (loading) return <div className="loading">Loading skills...</div>;

  return (
    <div className="skills-manager fade-in">
        <h1 className="mb-6">Manage Your Profile & Skills</h1>

        {/* PROFILE SECTION */}
        <section className="card p-6 mb-8 bg-white shadow-sm border border-slate-200">
             <h2 className="text-xl font-bold mb-4 flex items-center gap-2">
                 üìÇ Portfolio & Resume
             </h2>
             <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                 <div>
                     <label className="block text-sm font-bold text-slate-700 mb-2">Portfolio URL</label>
                     <input 
                        type="url" 
                        className="input-field w-full" 
                        placeholder="https://myportfolio.com"
                        value={portfolioUrl}
                        onChange={(e) => setPortfolioUrl(e.target.value)}
                     />
                 </div>
                 <div>
                     <label className="block text-sm font-bold text-slate-700 mb-2">GitHub URL</label>
                     <input 
                        type="url" 
                        className="input-field w-full" 
                        placeholder="https://github.com/username"
                        value={githubUrl}
                        onChange={(e) => setGithubUrl(e.target.value)}
                     />
                 </div>
                 <div className="md:col-span-2">
                     <label className="block text-sm font-bold text-slate-700 mb-2">Resume / CV</label>
                     <div className="flex gap-4 items-center">
                         <input 
                            type="file" 
                            className="file-input"
                            onChange={handleFileUpload}
                            accept=".pdf,.doc,.docx"
                         />
                         {uploading && <span className="text-sm text-primary">Uploading...</span>}
                         {resumeUrl && (
                             <a href={resumeUrl} target="_blank" rel="noreferrer" className="text-primary underline text-sm">
                                 View Current Resume
                             </a>
                         )}
                     </div>
                 </div>
             </div>
             <div className="mt-6 text-right">
                 <button className="btn btn-primary" onClick={handleSaveProfile}>
                     Save Profile Changes
                 </button>
             </div>
        </section>

      {/* SKILLS SECTION */}
      <section className="card p-6 bg-white shadow-sm border border-slate-200">
        <h2 className="text-xl font-bold mb-4 flex items-center gap-2">üõ†Ô∏è Skills Management</h2>
        
        {error && <div className="text-error mb-4">{error}</div>}

        {/* My Skills */}
        <div className="my-skills-section mb-8">
            <h3 className="text-lg font-semibold mb-4">Your Current Skills ({mySkills.length})</h3>
            {mySkills.length === 0 ? (
            <div className="text-slate-500 italic">
                <p>You haven't added any skills yet. Add some below!</p>
            </div>
            ) : (
            <div className="skills-grid">
                {mySkills.map((skill) => (
                <div key={skill.id} className="skill-card">
                    <div className="skill-header">
                    <h3>{skill.name}</h3>
                    <button
                        className="remove-btn"
                        onClick={() => removeSkill(skill.id, skill.name)}
                        title={`Remove ${skill.name}`}
                    >
                        √ó
                    </button>
                    </div>

                    <div className="proficiency-selector">
                        <label>Proficiency:</label>
                        <select
                            value={skill.proficiency}
                            onChange={(e) =>
                            updateProficiency(skill.id, e.target.value, skill.name)
                            }
                        >
                            <option value="Beginner">Beginner</option>
                            <option value="Intermediate">Intermediate</option>
                            <option value="Advanced">Advanced</option>
                        </select>
                    </div>

                    <div className={`proficiency-badge ${skill.proficiency.toLowerCase()}`}>
                        {skill.proficiency}
                    </div>
                </div>
                ))}
            </div>
            )}
        </div>

        {/* Add Skills */}
        <div className="add-skills-section">
            <h3 className="text-lg font-semibold mb-4">Add New Skills</h3>
            <div className="flex gap-4 mb-4">
                <input
                    type="text"
                    className="input-field flex-1"
                    placeholder="Search skills..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
                <select
                    className="input-field w-40"
                    value={selectedProficiency}
                    onChange={(e) => setSelectedProficiency(e.target.value)}
                >
                    <option value="Beginner">Beginner</option>
                    <option value="Intermediate">Intermediate</option>
                    <option value="Advanced">Advanced</option>
                </select>
            </div>

            <div className="available-skills">
                {availableSkills.length === 0 ? (
                <p className="text-slate-400 text-sm">
                    {searchTerm ? "No skills found" : "Type to search..."}
                </p>
                ) : (
                availableSkills.map((skill) => (
                    <button
                        key={skill.id}
                        className="skill-btn"
                        onClick={() => addSkill(skill.id, skill.name)}
                        title={`Add ${skill.name} as ${selectedProficiency}`}
                    >
                        {skill.name}
                        <span className="proficiency-preview">
                        ({selectedProficiency})
                        </span>
                    </button>
                ))
                )}
            </div>
        </div>
      </section>
    </div>
  );
}

export default SkillsManager;
