import React from "react";
import { Link, useLocation, useNavigate } from "react-router-dom";
import "./Sidebar.css";

const Sidebar = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem("user"));
  const role = user?.role || "student";

  const handleLogout = () => {
    localStorage.clear();
    navigate("/login");
  };

  const menuItems = {
    student: [
      { path: "/dashboard", label: "Dashboard", icon: "üìä" },
      { path: "/skills", label: "My Skills", icon: "üíº" },
      { path: "/jobs", label: "Find Jobs", icon: "üîç" },
      { path: "/ai", label: "AI Tools", icon: "ü§ñ" },
      { path: "/applications", label: "Applications", icon: "üìÑ" },
    ],
    recruiter: [
      { path: "/dashboard", label: "Dashboard", icon: "üìä" },
      { path: "/ai", label: "AI Tools", icon: "ü§ñ" },
      { path: "/post-job", label: "Post a Job", icon: "üìù" },
      { path: "/my-jobs", label: "My Jobs", icon: "üíº" },
    ],
    admin: [
      { path: "/admin", label: "Dashboard", icon: "üìä" },
      { path: "/ai", label: "AI Tools", icon: "ü§ñ" },
      { path: "/admin/users", label: "Manage Users", icon: "üë•" },
      { path: "/admin/jobs", label: "Manage Jobs", icon: "üíº" },
    ],
  };

  const currentItems = menuItems[role] || [];

  return (
    <aside className="sidebar">
      <div className="sidebar-branding">
        <div className="sidebar-logo">
          <div className="logo-icon">‚ö°</div>
          SkillBridge
        </div>
      </div>

      <nav className="sidebar-nav">
        <div className="nav-section-label">Main Menu</div>
        {currentItems.map((item) => (
          <Link
            key={item.path}
            to={item.path}
            className={`sidebar-item ${location.pathname === item.path ? "active" : ""}`}
          >
            <span className="nav-icon">{item.icon}</span>
            <span className="nav-label">{item.label}</span>
          </Link>
        ))}
      </nav>

      <div className="sidebar-footer">
        <div className="sidebar-logout-card" onClick={handleLogout}>
          <div className="logout-icon-bg">üö™</div>
          <div className="logout-text">
            <span className="logout-title">Sign Out</span>
            <span className="logout-subtitle">{user?.name}</span>
          </div>
        </div>
      </div>
    </aside>
  );
};

export default Sidebar;
